function pagination(row_num, id, lookup) {
	var req_num_row = row_num;
	var filter = "";
	var table = document.getElementById("csvtable_" + id);
	var pagination_selector = document.getElementById("csvpagination_" + id);

	if (lookup) {
		input = document.getElementById("csvlookup_" + id);
		filter = input.value.toUpperCase();
	}
	if (filter.length === 0) jQuery("tr", table).addClass("rowshow");
	var $tr = jQuery("tr.rowshow", table);

	var total_num_row = $tr.length;
	var num_pages = 0;
	if (total_num_row % req_num_row == 0) {
		num_pages = total_num_row / req_num_row;
	}
	if (total_num_row % req_num_row >= 1) {
		num_pages = total_num_row / req_num_row;
		num_pages++;
		num_pages = Math.floor(num_pages++);
	}
	for (var i = 1; i <= num_pages; i++) {
		jQuery(pagination_selector).append("<a href='#' class='btn'>" + i + "</a>");
	}
	$tr.each(function (i) {
		jQuery(this).hide();
		if (i + 1 <= req_num_row) {
			$tr.eq(i).show();
		}
	});
	jQuery("a", pagination_selector).click(function (e) {
		jQuery("a", pagination_selector).removeClass("active");
		jQuery(this).addClass("active");
		e.preventDefault();
		$tr.hide();
		var page = jQuery(this).text();
		var temp = page - 1;
		var start = temp * req_num_row;
		//alert(start);

		for (var i = 0; i < req_num_row; i++) {
			$tr.eq(start + i).show();
		}
	});
	jQuery("a:first", pagination_selector).addClass("active");
}
function lookuptable(row_num, min_char, id) {
	// Declare variables
	var input, filter, table, tr, td, i, j;
	input = document.getElementById("csvlookup_" + id);
	filter = input.value.toUpperCase();
	table = document.getElementById("csvtable_" + id);
	tr = table.getElementsByTagName("tr");
	if (typeof row_num != "number") row_num = 10;
	if (typeof min_char != "number") min_char = 1;

	// Loop through all table rows, and hide those who don't match the search query
	if (filter.length >= min_char) {
		for (i = 1; i < tr.length; i++) {
			td = tr[i].getElementsByTagName("td");

			for (j = 0; j < td.length; j++) {
				let tdata = td[j];
				if (tdata) {
					if (tdata.innerHTML.toUpperCase().indexOf(filter) > -1) {
						tr[i].classList.add("rowshow");
						tr[i].style.display = "";
						break;
					} else {
						tr[i].classList.remove("rowshow");
						tr[i].style.display = "none";
					}
				}
			}
		}
	} else {
		// for (i = 1; i < tr.length; i++) {
		//   tr[i].classList.add('rowshow');
		//    }
	}
	jQuery("#csvpagination_" + id + " a").remove(".btn");
}
